mapper.select:
select permission
from menu_permission
where biz_type = #{sysType}

select mp.permission
    from menu_permission mp, role_menu rm, user_role ur
    where mp.menu_permission_id = rm.menu_permission_id and rm.role_id = ur.role_id and ur.user_id = #{userId}

select permission, uri, method
from menu_permission
where biz_type = #{sysType}
